{
  "description": "The tag to create.",
  "required": true,
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "data": {
            "allOf": [
              {
                "allOf": [
                  {
                    "description": "A *tag* is a label that can be attached to any task in Asana. It exists in a single workspace or organization.",
                    "type": "object",
                    "properties": {
                      "gid": {
                        "description": "Globally unique identifier of the resource, as a string.",
                        "type": "string",
                        "readOnly": true,
                        "example": "12345",
                        "x-insert-after": false
                      },
                      "resource_type": {
                        "description": "The base type of this resource.",
                        "type": "string",
                        "readOnly": true,
                        "example": "tag",
                        "x-insert-after": "gid"
                      },
                      "name": {
                        "description": "Name of the tag. This is generally a short sentence fragment that fits on a line in the UI for maximum readability. However, it can be longer.",
                        "type": "string",
                        "example": "Stuff to buy"
                      }
                    }
                  },
                  {
                    "type": "object",
                    "properties": {
                      "color": {
                        "type": "string",
                        "description": "Color of the tag.",
                        "nullable": true,
                        "enum": [
                          "dark-pink",
                          "dark-green",
                          "dark-blue",
                          "dark-red",
                          "dark-teal",
                          "dark-brown",
                          "dark-orange",
                          "dark-purple",
                          "dark-warm-gray",
                          "light-pink",
                          "light-green",
                          "light-blue",
                          "light-red",
                          "light-teal",
                          "light-brown",
                          "light-orange",
                          "light-purple",
                          "light-warm-gray",
                          null
                        ],
                        "example": "light-green"
                      },
                      "notes": {
                        "description": "Free-form textual information associated with the tag (i.e. its description).",
                        "type": "string",
                        "example": "Mittens really likes the stuff from Humboldt."
                      }
                    }
                  }
                ]
              },
              {
                "type": "object",
                "properties": {
                  "followers": {
                    "type": "array",
                    "description": "An array of strings identifying users. These can either be the string \"me\", an email, or the gid of a user.",
                    "items": {
                      "type": "string"
                    },
                    "example": [
                      "12345",
                      "42563"
                    ]
                  },
                  "workspace": {
                    "type": "string",
                    "x-env-variable": true,
                    "description": "Gid of an object.",
                    "example": "12345"
                  }
                }
              }
            ]
          }
        }
      }
    }
  }
}

