http_endpoint.request_body_spec:
{
  "description": "Information about the custom field setting being removed.",
  "required": true,
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "data": {
            "type": "object",
            "required": [
              "custom_field"
            ],
            "properties": {
              "custom_field": {
                "description": "The custom field to remove from this portfolio.",
                "type": "string",
                "example": "14916"
              }
            }
          }
        }
      }
    }
  }
}
