http_endpoint.request_body_spec:
{
  "description": "The updated fields for the time tracking entry.",
  "required": true,
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "data": {
            "type": "object",
            "properties": {
              "duration_minutes": {
                "description": "*Optional*. Time in minutes tracked by the entry",
                "type": "integer",
                "example": 12
              },
              "entered_on": {
                "description": "*Optional*. The day that this entry is logged on. Defaults to today if no day specified",
                "type": "string",
                "format": "date",
                "example": "2023-03-19"
              },
              "attributable_to": {
                "type": "string",
                "description": "*Optional*. The gid of the project which the time is attributable to.",
                "example": "987654"
              }
            }
          }
        }
      }
    }
  }
}
