http_endpoint.request_body_spec:
{
  "description": "The workspace object with all updated properties.",
  "required": true,
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "data": {
            "description": "A *workspace* is the highest-level organizational unit in Asana. All projects and tasks have an associated workspace.",
            "type": "object",
            "properties": {
              "gid": {
                "description": "Globally unique identifier of the resource, as a string.",
                "type": "string",
                "readOnly": true,
                "example": "12345",
                "x-insert-after": false
              },
              "resource_type": {
                "description": "The base type of this resource.",
                "type": "string",
                "readOnly": true,
                "example": "workspace",
                "x-insert-after": "gid"
              },
              "name": {
                "description": "The name of the workspace.",
                "type": "string",
                "example": "My Company Workspace"
              }
            }
          }
        }
      }
    }
  }
}
