{
  "description": "The group handle can be updated, which is used to @mention a User Group within the Workspace.\\\n \\\nModify Group members by using the \"add\" and \"rem\" parameters with an array of user IDs to include or exclude members.",
  "content": {
    "application/json": {
      "schema": {
        "title": "UpdateTeamrequest",
        "type": "object",
        "properties": {
          "name": {
            "type": "string"
          },
          "handle": {
            "type": "string"
          },
          "members": {
            "title": "Members2",
            "required": [
              "add",
              "rem"
            ],
            "type": "object",
            "properties": {
              "add": {
                "type": "array",
                "items": {
                  "type": "integer",
                  "contentEncoding": "int32"
                },
                "description": ""
              },
              "rem": {
                "type": "array",
                "items": {
                  "type": "integer",
                  "contentEncoding": "int32"
                },
                "description": ""
              }
            },
            "examples": [
              {
                "add": [
                  123456,
                  987654
                ],
                "rem": [
                  159753
                ]
              }
            ]
          }
        },
        "examples": [
          {
            "name": "New User Group Name",
            "handle": "newusergroupname",
            "members": {
              "add": [
                123456,
                987654
              ],
              "rem": [
                159753
              ]
            }
          }
        ]
      },
      "example": {
        "name": "New User Group Name",
        "handle": "newusergroupname",
        "members": {
          "add": [
            123456,
            987654
          ],
          "rem": [
            159753
          ]
        }
      }
    }
  },
  "required": true
}
