http_endpoint.request_body_spec:
{
  "required": true,
  "content": {
    "application/json": {
      "schema": {
        "discriminator": {
          "propertyName": "format"
        },
        "oneOf": [
          {
            "type": [
              "object"
            ],
            "properties": {
              "size": {
                "type": [
                  "number"
                ],
                "maximum": 50000000,
                "description": "The size of the file in bytes. Maximum size is 50 MB."
              },
              "count": {
                "type": [
                  "number"
                ],
                "description": "The number of records to import"
              },
              "title": {
                "type": [
                  "string"
                ],
                "description": "The title of the import"
              },
              "format": {
                "type": [
                  "string"
                ],
                "enum": [
                  "csv"
                ],
                "description": "When the import is in CSV format, we expect a column containing the key identifier for the entity - for now URL. If not provided, import will fail to be processed."
              },
              "metadata": {
                "description": "Set of key-value pairs you want to associate with this object.",
                "type": [
                  "object"
                ],
                "additionalProperties": {
                  "type": [
                    "string"
                  ],
                  "maxLength": 1000
                }
              },
              "entity": {
                "oneOf": [
                  {
                    "type": [
                      "object"
                    ],
                    "properties": {
                      "type": {
                        "type": "string",
                        "const": "company",
                        "default": "company"
                      }
                    },
                    "required": [
                      "type"
                    ],
                    "title": "Company"
                  },
                  {
                    "type": [
                      "object"
                    ],
                    "properties": {
                      "type": {
                        "type": "string",
                        "const": "person",
                        "default": "person"
                      }
                    },
                    "required": [
                      "type"
                    ],
                    "title": "Person"
                  },
                  {
                    "type": [
                      "object"
                    ],
                    "properties": {
                      "type": {
                        "type": "string",
                        "const": "article",
                        "default": "article"
                      }
                    },
                    "required": [
                      "type"
                    ],
                    "title": "Article"
                  },
                  {
                    "type": [
                      "object"
                    ],
                    "properties": {
                      "type": {
                        "type": "string",
                        "const": "research_paper",
                        "default": "research_paper"
                      }
                    },
                    "required": [
                      "type"
                    ],
                    "title": "Research Paper"
                  },
                  {
                    "type": [
                      "object"
                    ],
                    "properties": {
                      "type": {
                        "type": "string",
                        "const": "custom",
                        "default": "custom"
                      },
                      "description": {
                        "type": [
                          "string"
                        ],
                        "minLength": 2,
                        "maxLength": 200
                      }
                    },
                    "required": [
                      "type",
                      "description"
                    ],
                    "title": "Custom"
                  }
                ],
                "description": "What type of entity the import contains (e.g. People, Companies, etc.), and thus should be attempted to be resolved as."
              },
              "csv": {
                "type": [
                  "object"
                ],
                "properties": {
                  "identifier": {
                    "type": [
                      "integer"
                    ],
                    "minimum": 0,
                    "description": "Column containing the key identifier for the entity (e.g. URL, Name, etc.). If not provided, we will try to infer it from the file."
                  }
                },
                "description": "When format is `csv`, these are the specific import parameters."
              }
            },
            "required": [
              "size",
              "count",
              "format",
              "entity"
            ]
          }
        ]
      }
    }
  }
}
