http_endpoint.request_body_spec:
{
  "required": true,
  "content": {
    "application/json": {
      "schema": {
        "type": [
          "object"
        ],
        "properties": {
          "model": {
            "default": "exa-research",
            "type": [
              "string"
            ],
            "enum": [
              "exa-research",
              "exa-research-pro"
            ]
          },
          "instructions": {
            "type": [
              "string"
            ],
            "maxLength": 4096,
            "description": "Instructions for what research should be conducted"
          },
          "outputSchema": {
            "type": [
              "object"
            ],
            "additionalProperties": {}
          }
        },
        "required": [
          "instructions"
        ],
        "examples": [
          {
            "model": "exa-research",
            "instructions": "What species of ant are similar to honeypot ants?"
          }
        ]
      }
    }
  }
}
