http_endpoint.request_body_spec:
{
  "required": true,
  "content": {
    "application/json": {
      "schema": {
        "type": [
          "object"
        ],
        "properties": {
          "websetId": {
            "type": [
              "string"
            ],
            "description": "The id of the Webset"
          },
          "cadence": {
            "type": [
              "object"
            ],
            "properties": {
              "cron": {
                "description": "Cron expression for monitor cadence (must be a valid Unix cron with 5 fields). The schedule must trigger at most once per day.",
                "type": [
                  "string"
                ]
              },
              "timezone": {
                "description": "IANA timezone (e.g., \"America/New_York\")",
                "default": "Etc/UTC",
                "type": [
                  "string"
                ]
              }
            },
            "required": [
              "cron"
            ],
            "description": "How often the monitor will run"
          },
          "behavior": {
            "type": [
              "object"
            ],
            "properties": {
              "type": {
                "type": "string",
                "const": "search",
                "default": "search"
              },
              "config": {
                "type": [
                  "object"
                ],
                "properties": {
                  "query": {
                    "type": [
                      "string"
                    ],
                    "minLength": 2,
                    "maxLength": 10000,
                    "description": "The query to search for. By default, the query from the last search is used."
                  },
                  "criteria": {
                    "type": [
                      "array"
                    ],
                    "items": {
                      "type": [
                        "object"
                      ],
                      "properties": {
                        "description": {
                          "type": [
                            "string"
                          ],
                          "minLength": 2,
                          "maxLength": 1000
                        }
                      },
                      "required": [
                        "description"
                      ]
                    },
                    "maxItems": 5,
                    "description": "The criteria to search for. By default, the criteria from the last search is used."
                  },
                  "entity": {
                    "oneOf": [
                      {
                        "type": [
                          "object"
                        ],
                        "properties": {
                          "type": {
                            "type": "string",
                            "const": "company",
                            "default": "company"
                          }
                        },
                        "required": [
                          "type"
                        ],
                        "title": "Company"
                      },
                      {
                        "type": [
                          "object"
                        ],
                        "properties": {
                          "type": {
                            "type": "string",
                            "const": "person",
                            "default": "person"
                          }
                        },
                        "required": [
                          "type"
                        ],
                        "title": "Person"
                      },
                      {
                        "type": [
                          "object"
                        ],
                        "properties": {
                          "type": {
                            "type": "string",
                            "const": "article",
                            "default": "article"
                          }
                        },
                        "required": [
                          "type"
                        ],
                        "title": "Article"
                      },
                      {
                        "type": [
                          "object"
                        ],
                        "properties": {
                          "type": {
                            "type": "string",
                            "const": "research_paper",
                            "default": "research_paper"
                          }
                        },
                        "required": [
                          "type"
                        ],
                        "title": "Research Paper"
                      },
                      {
                        "type": [
                          "object"
                        ],
                        "properties": {
                          "type": {
                            "type": "string",
                            "const": "custom",
                            "default": "custom"
                          },
                          "description": {
                            "type": [
                              "string"
                            ],
                            "minLength": 2,
                            "maxLength": 200
                          }
                        },
                        "required": [
                          "type",
                          "description"
                        ],
                        "title": "Custom"
                      }
                    ]
                  },
                  "count": {
                    "type": [
                      "number"
                    ],
                    "exclusiveMinimum": 0,
                    "description": "The maximum number of results to find"
                  },
                  "behavior": {
                    "default": "append",
                    "type": [
                      "string"
                    ],
                    "enum": [
                      "override",
                      "append"
                    ],
                    "description": "The behaviour of the Search when it is added to a Webset."
                  }
                },
                "required": [
                  "count"
                ],
                "description": "Specify the search parameters for the Monitor.\n\nBy default, the search parameters (query, entity and criteria) from the last search are used when no parameters are provided."
              }
            },
            "required": [
              "type",
              "config"
            ],
            "description": "Behavior to perform when monitor runs"
          },
          "metadata": {
            "type": [
              "object"
            ],
            "additionalProperties": {
              "type": [
                "string"
              ]
            }
          }
        },
        "required": [
          "websetId",
          "cadence",
          "behavior"
        ]
      }
    }
  }
}
