http_endpoint.request_body_spec:
{
  "required": true,
  "content": {
    "application/json": {
      "schema": {
        "type": [
          "object"
        ],
        "properties": {
          "query": {
            "type": [
              "string"
            ],
            "minLength": 1,
            "maxLength": 5000,
            "description": "Natural language search query describing what you are looking for.\n\nBe specific and descriptive about your requirements, characteristics, and any constraints that help narrow down the results.",
            "examples": [
              "Marketing agencies based in the US, that focus on consumer products. Get brands worked with and city",
              "AI startups in Europe that raised Series A funding in 2024",
              "SaaS companies with 50-200 employees in the fintech space"
            ]
          },
          "entity": {
            "oneOf": [
              {
                "type": [
                  "object"
                ],
                "properties": {
                  "type": {
                    "type": "string",
                    "const": "company",
                    "default": "company"
                  }
                },
                "required": [
                  "type"
                ],
                "title": "Company"
              },
              {
                "type": [
                  "object"
                ],
                "properties": {
                  "type": {
                    "type": "string",
                    "const": "person",
                    "default": "person"
                  }
                },
                "required": [
                  "type"
                ],
                "title": "Person"
              },
              {
                "type": [
                  "object"
                ],
                "properties": {
                  "type": {
                    "type": "string",
                    "const": "article",
                    "default": "article"
                  }
                },
                "required": [
                  "type"
                ],
                "title": "Article"
              },
              {
                "type": [
                  "object"
                ],
                "properties": {
                  "type": {
                    "type": "string",
                    "const": "research_paper",
                    "default": "research_paper"
                  }
                },
                "required": [
                  "type"
                ],
                "title": "Research Paper"
              },
              {
                "type": [
                  "object"
                ],
                "properties": {
                  "type": {
                    "type": "string",
                    "const": "custom",
                    "default": "custom"
                  },
                  "description": {
                    "type": [
                      "string"
                    ],
                    "minLength": 2,
                    "maxLength": 200
                  }
                },
                "required": [
                  "type",
                  "description"
                ],
                "title": "Custom"
              }
            ]
          }
        },
        "required": [
          "query"
        ]
      }
    }
  }
}
