http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "examples": {
        "default": {
          "value": {
            "apps": [
              "octoapp"
            ]
          }
        }
      },
      "schema": {
        "oneOf": [
          {
            "example": {
              "apps": [
                "my-app"
              ]
            },
            "properties": {
              "apps": {
                "description": "The GitHub Apps that have push access to this branch. Use the slugified version of the app name. **Note**: The list of users, apps, and teams in total is limited to 100 items.",
                "items": {
                  "type": "string"
                },
                "type": "array"
              }
            },
            "required": [
              "apps"
            ],
            "type": "object"
          },
          {
            "items": {
              "type": "string"
            },
            "type": "array"
          }
        ]
      }
    }
  },
  "required": false
}
