http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "examples": {
        "default": {
          "value": {
            "content": "heart"
          }
        }
      },
      "schema": {
        "properties": {
          "content": {
            "description": "The [reaction type](https://docs.github.com/enterprise-server@3.8/rest/reference/reactions#reaction-types) to add to the commit comment.",
            "enum": [
              "+1",
              "-1",
              "laugh",
              "confused",
              "heart",
              "hooray",
              "rocket",
              "eyes"
            ],
            "type": "string"
          }
        },
        "required": [
          "content"
        ],
        "type": "object"
      }
    }
  },
  "required": true
}
