http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "examples": {
        "default": {
          "value": {
            "comment": "Ship it!",
            "environment_ids": [
              161171787
            ],
            "state": "approved"
          }
        }
      },
      "schema": {
        "properties": {
          "comment": {
            "description": "A comment to accompany the deployment review",
            "example": "Ship it!",
            "type": "string"
          },
          "environment_ids": {
            "description": "The list of environment ids to approve or reject",
            "example": [
              161171787,
              161171795
            ],
            "items": {
              "example": 161171787,
              "type": "integer"
            },
            "type": "array"
          },
          "state": {
            "description": "Whether to approve or reject deployment to the specified environments.",
            "enum": [
              "approved",
              "rejected"
            ],
            "example": "approved",
            "type": "string"
          }
        },
        "required": [
          "environment_ids",
          "state",
          "comment"
        ],
        "type": "object"
      }
    }
  },
  "required": true
}
