http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "examples": {
        "default": {
          "value": {
            "assignees": [
              "hubot",
              "other_user"
            ]
          }
        }
      },
      "schema": {
        "properties": {
          "assignees": {
            "description": "Usernames of people to assign this issue to. _NOTE: Only users with push access can add assignees to an issue. Assignees are silently ignored otherwise._",
            "items": {
              "type": "string"
            },
            "type": "array"
          }
        },
        "type": "object"
      }
    }
  },
  "required": false
}
