http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "examples": {
        "group": {
          "value": {
            "displayName": "Engineering",
            "externalId": "8aa1a0c0-c4c3-4bc0-b4a5-2ef676900159",
            "schemas": [
              "urn:ietf:params:scim:schemas:core:2.0:Group"
            ]
          }
        }
      },
      "schema": {
        "properties": {
          "displayName": {
            "description": "A human-readable name for a security group.",
            "example": "Engineering",
            "type": "string"
          },
          "externalId": {
            "description": "A unique identifier for the resource as defined by the provisioning client.",
            "example": "8aa1a0c0-c4c3-4bc0-b4a5-2ef676900159",
            "type": "string"
          },
          "members": {
            "description": "The group members.",
            "items": {
              "properties": {
                "displayName": {
                  "description": "The display name associated with the member",
                  "example": "Monalisa Octocat",
                  "type": "string"
                },
                "value": {
                  "description": "The local unique identifier for the member",
                  "example": "23a35c27-23d3-4c03-b4c5-6443c09e7173",
                  "type": "string"
                }
              },
              "required": [
                "value",
                "displayName"
              ],
              "type": "object"
            },
            "type": "array"
          },
          "schemas": {
            "description": "The URIs that are used to indicate the namespaces of the SCIM schemas.",
            "example": [
              "urn:ietf:params:scim:schemas:core:2.0:Group"
            ],
            "items": {
              "enum": [
                "urn:ietf:params:scim:schemas:core:2.0:Group"
              ],
              "type": "string"
            },
            "type": "array"
          }
        },
        "required": [
          "schemas",
          "externalId",
          "displayName",
          "members"
        ],
        "type": "object"
      }
    }
  },
  "required": true
}
