http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "examples": {
        "default": {
          "value": {
            "content": "Content of the blob",
            "encoding": "utf-8"
          }
        }
      },
      "schema": {
        "properties": {
          "content": {
            "description": "The new blob's content.",
            "type": "string"
          },
          "encoding": {
            "default": "utf-8",
            "description": "The encoding used for `content`. Currently, `\"utf-8\"` and `\"base64\"` are supported.",
            "type": "string"
          }
        },
        "required": [
          "content"
        ],
        "type": "object"
      }
    }
  },
  "required": true
}
