http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "examples": {
        "default": {
          "value": {
            "assignees": [
              "octocat"
            ],
            "body": "I'm having a problem with this.",
            "labels": [
              "bug"
            ],
            "milestone": 1,
            "title": "Found a bug"
          }
        }
      },
      "schema": {
        "properties": {
          "assignee": {
            "description": "Login for the user that this issue should be assigned to. _NOTE: Only users with push access can set the assignee for new issues. The assignee is silently dropped otherwise. **This field is deprecated.**_",
            "nullable": true,
            "type": "string"
          },
          "assignees": {
            "description": "Logins for Users to assign to this issue. _NOTE: Only users with push access can set assignees for new issues. Assignees are silently dropped otherwise._",
            "items": {
              "type": "string"
            },
            "type": "array"
          },
          "body": {
            "description": "The contents of the issue.",
            "type": "string"
          },
          "labels": {
            "description": "Labels to associate with this issue. _NOTE: Only users with push access can set labels for new issues. Labels are silently dropped otherwise._",
            "items": {
              "oneOf": [
                {
                  "type": "string"
                },
                {
                  "properties": {
                    "color": {
                      "nullable": true,
                      "type": "string"
                    },
                    "description": {
                      "nullable": true,
                      "type": "string"
                    },
                    "id": {
                      "type": "integer"
                    },
                    "name": {
                      "type": "string"
                    }
                  },
                  "type": "object"
                }
              ]
            },
            "type": "array"
          },
          "milestone": {
            "nullable": true,
            "oneOf": [
              {
                "type": "string"
              },
              {
                "description": "The `number` of the milestone to associate this issue with. _NOTE: Only users with push access can set the milestone for new issues. The milestone is silently dropped otherwise._",
                "type": "integer"
              }
            ]
          },
          "title": {
            "description": "The title of the issue.",
            "oneOf": [
              {
                "type": "string"
              },
              {
                "type": "integer"
              }
            ]
          }
        },
        "required": [
          "title"
        ],
        "type": "object"
      }
    }
  },
  "required": true
}
