http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "examples": {
        "default": {
          "value": {
            "encrypted_value": "c2VjcmV0",
            "key_id": "012345678912345678"
          }
        }
      },
      "schema": {
        "properties": {
          "encrypted_value": {
            "description": "Value for your secret, encrypted with [LibSodium](https://libsodium.gitbook.io/doc/bindings_for_other_languages) using the public key retrieved from the [Get an environment public key](https://docs.github.com/enterprise-server@3.8/rest/reference/actions#get-an-environment-public-key) endpoint.",
            "pattern": "^(?:[A-Za-z0-9+/]{4})*(?:[A-Za-z0-9+/]{2}==|[A-Za-z0-9+/]{3}=|[A-Za-z0-9+/]{4})$",
            "type": "string"
          },
          "key_id": {
            "description": "ID of the key you used to encrypt the secret.",
            "type": "string"
          }
        },
        "required": [
          "encrypted_value",
          "key_id"
        ],
        "type": "object"
      }
    }
  },
  "required": true
}
