http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "examples": {
        "default": {
          "value": {
            "reviewers": [
              "octocat",
              "hubot",
              "other_user"
            ],
            "team_reviewers": [
              "justice-league"
            ]
          }
        }
      },
      "schema": {
        "anyOf": [
          {
            "required": [
              "reviewers"
            ]
          },
          {
            "required": [
              "team_reviewers"
            ]
          }
        ],
        "properties": {
          "reviewers": {
            "description": "An array of user `login`s that will be requested.",
            "items": {
              "type": "string"
            },
            "type": "array"
          },
          "team_reviewers": {
            "description": "An array of team `slug`s that will be requested.",
            "items": {
              "type": "string"
            },
            "type": "array"
          }
        },
        "type": "object"
      }
    }
  },
  "required": false
}
