http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "examples": {
        "default": {
          "value": {
            "announcement": "Very **important** announcement about _something_.",
            "expires_at": "2021-01-01T00:00:00+00:00"
          }
        }
      },
      "schema": {
        "description": "Enterprise global announcement",
        "properties": {
          "announcement": {
            "description": "The announcement text in GitHub Flavored Markdown. For more information about GitHub Flavored Markdown, see \"[Basic writing and formatting syntax](https://docs.github.com/enterprise-server@3.8/github/writing-on-github/getting-started-with-writing-and-formatting-on-github/basic-writing-and-formatting-syntax).\"",
            "example": "Very **important** announcement about _something_.",
            "nullable": true,
            "type": "string"
          },
          "expires_at": {
            "description": "The time at which the announcement expires. This is a timestamp in [ISO 8601](https://en.wikipedia.org/wiki/ISO_8601) format: `YYYY-MM-DDTHH:MM:SSZ`. To set an announcement that never expires, omit this parameter, set it to `null`, or set it to an empty string.",
            "example": "\"2021-01-01T00:00:00.000-07:00\"",
            "format": "date-time",
            "nullable": true,
            "type": "string"
          }
        },
        "required": [
          "announcement"
        ],
        "title": "Enterprise Announcement",
        "type": "object"
      }
    }
  },
  "required": true
}
