http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "examples": {
        "default": {
          "value": {
            "ignored": false,
            "subscribed": true
          }
        }
      },
      "schema": {
        "properties": {
          "ignored": {
            "description": "Determines if all notifications should be blocked from this repository.",
            "type": "boolean"
          },
          "subscribed": {
            "description": "Determines if notifications should be received from this repository.",
            "type": "boolean"
          }
        },
        "type": "object"
      }
    }
  },
  "required": false
}
