http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "examples": {
        "disableUser": {
          "summary": "Disable User",
          "value": {
            "Operations": [
              {
                "op": "replace",
                "path": "active",
                "value": false
              }
            ],
            "schemas": [
              "urn:ietf:params:scim:api:messages:2.0:PatchOp"
            ]
          }
        },
        "userMultiValuedProperties": {
          "summary": "Multi Valued Property",
          "value": {
            "Operations": [
              {
                "op": "replace",
                "path": "emails[type eq 'work'].value",
                "value": "updatedEmail@microsoft.com"
              },
              {
                "op": "replace",
                "path": "name.familyName",
                "value": "updatedFamilyName"
              }
            ],
            "schemas": [
              "urn:ietf:params:scim:api:messages:2.0:PatchOp"
            ]
          }
        },
        "userSingleValuedProperties": {
          "summary": "Single Valued Property",
          "value": {
            "Operations": [
              {
                "op": "replace",
                "path": "userName",
                "value": "5b50642d-79fc-4410-9e90-4c077cdd1a59@testuser.com"
              },
              {
                "op": "replace",
                "path": "displayName",
                "value": "Monalisa Octocat"
              }
            ],
            "schemas": [
              "urn:ietf:params:scim:api:messages:2.0:PatchOp"
            ]
          }
        }
      },
      "schema": {
        "properties": {
          "Operations": {
            "description": "patch operations list",
            "items": {
              "properties": {
                "op": {
                  "enum": [
                    "add",
                    "replace",
                    "remove"
                  ],
                  "type": "string"
                },
                "path": {
                  "type": "string"
                },
                "value": {
                  "description": "Corresponding 'value' of that field specified by 'path'",
                  "type": "string"
                }
              },
              "required": [
                "op"
              ],
              "type": "object"
            },
            "type": "array"
          },
          "schemas": {
            "items": {
              "enum": [
                "urn:ietf:params:scim:api:messages:2.0:PatchOp"
              ],
              "type": "string"
            },
            "type": "array"
          }
        },
        "required": [
          "Operations",
          "schemas"
        ],
        "type": "object"
      }
    }
  },
  "required": true
}
