http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "examples": {
        "deleteFile": {
          "summary": "Deleting a gist file",
          "value": {
            "files": {
              "hello.py": null
            }
          }
        },
        "renameFile": {
          "summary": "Renaming a gist file",
          "value": {
            "files": {
              "hello.py": {
                "filename": "goodbye.py"
              }
            }
          }
        },
        "updateGist": {
          "summary": "Updating a gist",
          "value": {
            "description": "An updated gist description",
            "files": {
              "README.md": {
                "content": "Hello World from GitHub"
              }
            }
          }
        }
      },
      "schema": {
        "anyOf": [
          {
            "required": [
              "description"
            ]
          },
          {
            "required": [
              "files"
            ]
          }
        ],
        "nullable": true,
        "properties": {
          "description": {
            "description": "The description of the gist.",
            "example": "Example Ruby script",
            "type": "string"
          },
          "files": {
            "additionalProperties": {
              "anyOf": [
                {
                  "required": [
                    "content"
                  ]
                },
                {
                  "required": [
                    "filename"
                  ]
                },
                {
                  "maxProperties": 0,
                  "type": "object"
                }
              ],
              "nullable": true,
              "properties": {
                "content": {
                  "description": "The new content of the file.",
                  "type": "string"
                },
                "filename": {
                  "description": "The new filename for the file.",
                  "nullable": true,
                  "type": "string"
                }
              },
              "type": "object"
            },
            "description": "The gist files to be updated, renamed, or deleted. Each `key` must match the current filename\n(including extension) of the targeted gist file. For example: `hello.py`.\n\nTo delete a file, set the whole file to null. For example: `hello.py : null`.",
            "example": {
              "hello.rb": {
                "content": "blah",
                "filename": "goodbye.rb"
              }
            },
            "type": "object"
          }
        },
        "type": "object"
      }
    }
  },
  "required": true
}
