http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "examples": {
        "default": {
          "value": {
            "cname": "octocatblog.com",
            "source": {
              "branch": "main",
              "path": "/"
            }
          }
        }
      },
      "schema": {
        "anyOf": [
          {
            "required": [
              "build_type"
            ]
          },
          {
            "required": [
              "source"
            ]
          },
          {
            "required": [
              "cname"
            ]
          },
          {
            "required": [
              "public"
            ]
          },
          {
            "required": [
              "https_enforced"
            ]
          }
        ],
        "properties": {
          "build_type": {
            "description": "The process by which the GitHub Pages site will be built. `workflow` means that the site is built by a custom GitHub Actions workflow. `legacy` means that the site is built by GitHub when changes are pushed to a specific branch.",
            "enum": [
              "legacy",
              "workflow"
            ],
            "type": "string"
          },
          "cname": {
            "description": "Specify a custom domain for the repository. Sending a `null` value will remove the custom domain. For more about custom domains, see \"[Using a custom domain with GitHub Pages](https://docs.github.com/enterprise-server@3.8/articles/using-a-custom-domain-with-github-pages/).\"",
            "nullable": true,
            "type": "string"
          },
          "https_enforced": {
            "description": "Specify whether HTTPS should be enforced for the repository.",
            "type": "boolean"
          },
          "source": {
            "anyOf": [
              {
                "description": "Update the source for the repository. Must include the branch name, and may optionally specify the subdirectory `/docs`. Possible values are `\"gh-pages\"`, `\"master\"`, and `\"master /docs\"`.",
                "enum": [
                  "gh-pages",
                  "master",
                  "master /docs"
                ],
                "type": "string"
              },
              {
                "description": "Update the source for the repository. Must include the branch name and path.",
                "properties": {
                  "branch": {
                    "description": "The repository branch used to publish your site's source files.",
                    "type": "string"
                  },
                  "path": {
                    "description": "The repository directory that includes the source files for the Pages site. Allowed paths are `/` or `/docs`.",
                    "enum": [
                      "/",
                      "/docs"
                    ],
                    "type": "string"
                  }
                },
                "required": [
                  "branch",
                  "path"
                ],
                "type": "object"
              }
            ]
          }
        },
        "type": "object"
      }
    }
  },
  "required": true
}
