http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "examples": {
        "default": {
          "value": {
            "description": "This is your first repository",
            "has_issues": true,
            "has_projects": true,
            "has_wiki": true,
            "homepage": "https://github.com",
            "name": "Hello-World",
            "private": true
          }
        }
      },
      "schema": {
        "properties": {
          "allow_forking": {
            "default": false,
            "description": "Either `true` to allow private forks, or `false` to prevent private forks.",
            "type": "boolean"
          },
          "allow_merge_commit": {
            "default": true,
            "description": "Either `true` to allow merging pull requests with a merge commit, or `false` to prevent merging pull requests with merge commits.",
            "type": "boolean"
          },
          "allow_rebase_merge": {
            "default": true,
            "description": "Either `true` to allow rebase-merging pull requests, or `false` to prevent rebase-merging.",
            "type": "boolean"
          },
          "allow_squash_merge": {
            "default": true,
            "description": "Either `true` to allow squash-merging pull requests, or `false` to prevent squash-merging.",
            "type": "boolean"
          },
          "allow_update_branch": {
            "default": false,
            "description": "Either `true` to always allow a pull request head branch that is behind its base branch to be updated even if it is not required to be up to date before merging, or false otherwise.",
            "type": "boolean"
          },
          "archived": {
            "default": false,
            "description": "Whether to archive this repository. `false` will unarchive a previously archived repository.",
            "type": "boolean"
          },
          "default_branch": {
            "description": "Updates the default branch for this repository.",
            "type": "string"
          },
          "delete_branch_on_merge": {
            "default": false,
            "description": "Either `true` to allow automatically deleting head branches when pull requests are merged, or `false` to prevent automatic deletion.",
            "type": "boolean"
          },
          "description": {
            "description": "A short description of the repository.",
            "type": "string"
          },
          "has_issues": {
            "default": true,
            "description": "Either `true` to enable issues for this repository or `false` to disable them.",
            "type": "boolean"
          },
          "has_projects": {
            "default": true,
            "description": "Either `true` to enable projects for this repository or `false` to disable them. **Note:** If you're creating a repository in an organization that has disabled repository projects, the default is `false`, and if you pass `true`, the API returns an error.",
            "type": "boolean"
          },
          "has_wiki": {
            "default": true,
            "description": "Either `true` to enable the wiki for this repository or `false` to disable it.",
            "type": "boolean"
          },
          "homepage": {
            "description": "A URL with more information about the repository.",
            "type": "string"
          },
          "is_template": {
            "default": false,
            "description": "Either `true` to make this repo available as a template repository or `false` to prevent it.",
            "type": "boolean"
          },
          "merge_commit_message": {
            "description": "The default value for a merge commit message.\n\n- `PR_TITLE` - default to the pull request's title.\n- `PR_BODY` - default to the pull request's body.\n- `BLANK` - default to a blank commit message.",
            "enum": [
              "PR_BODY",
              "PR_TITLE",
              "BLANK"
            ],
            "type": "string"
          },
          "merge_commit_title": {
            "description": "The default value for a merge commit title.\n\n- `PR_TITLE` - default to the pull request's title.\n- `MERGE_MESSAGE` - default to the classic title for a merge message (e.g., Merge pull request #123 from branch-name).",
            "enum": [
              "PR_TITLE",
              "MERGE_MESSAGE"
            ],
            "type": "string"
          },
          "name": {
            "description": "The name of the repository.",
            "type": "string"
          },
          "private": {
            "default": false,
            "description": "Either `true` to make the repository private or `false` to make it public. Default: `false`.  \n**Note**: You will get a `422` error if the organization restricts [changing repository visibility](https://docs.github.com/enterprise-server@3.8/articles/repository-permission-levels-for-an-organization#changing-the-visibility-of-repositories) to organization owners and a non-owner tries to change the value of private.",
            "type": "boolean"
          },
          "squash_merge_commit_message": {
            "description": "The default value for a squash merge commit message:\n\n- `PR_BODY` - default to the pull request's body.\n- `COMMIT_MESSAGES` - default to the branch's commit messages.\n- `BLANK` - default to a blank commit message.",
            "enum": [
              "PR_BODY",
              "COMMIT_MESSAGES",
              "BLANK"
            ],
            "type": "string"
          },
          "squash_merge_commit_title": {
            "description": "The default value for a squash merge commit title:\n\n- `PR_TITLE` - default to the pull request's title.\n- `COMMIT_OR_PR_TITLE` - default to the commit's title (if only one commit) or the pull request's title (when more than one commit).",
            "enum": [
              "PR_TITLE",
              "COMMIT_OR_PR_TITLE"
            ],
            "type": "string"
          },
          "use_squash_pr_title_as_default": {
            "default": false,
            "deprecated": true,
            "description": "Either `true` to allow squash-merge commits to use pull request title, or `false` to use commit message. **This property has been deprecated. Please use `squash_merge_commit_title` instead.",
            "type": "boolean"
          },
          "visibility": {
            "description": "The visibility of the repository.",
            "enum": [
              "public",
              "private",
              "internal"
            ],
            "type": "string"
          },
          "web_commit_signoff_required": {
            "default": false,
            "description": "Either `true` to require contributors to sign off on web-based commits, or `false` to not require contributors to sign off on web-based commits.",
            "type": "boolean"
          }
        },
        "type": "object"
      }
    }
  },
  "required": false
}
