http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "examples": {
        "user": {
          "$ref": "#/components/examples/in-user"
        }
      },
      "schema": {
        "properties": {
          "active": {
            "description": "Whether the user active in the IdP.",
            "example": true,
            "type": "boolean"
          },
          "displayName": {
            "description": "A human-readable name for the user.",
            "example": "Mona Lisa",
            "type": "string"
          },
          "emails": {
            "description": "The emails for the user.",
            "items": {
              "properties": {
                "primary": {
                  "description": "Whether this email address is the primary address.",
                  "example": true,
                  "type": "boolean"
                },
                "type": {
                  "description": "The type of email address.",
                  "example": "work",
                  "type": "string"
                },
                "value": {
                  "description": "The email address.",
                  "example": "mlisa@example.com",
                  "type": "string"
                }
              },
              "required": [
                "value",
                "type",
                "primary"
              ],
              "type": "object"
            },
            "type": "array"
          },
          "externalId": {
            "description": "A unique identifier for the resource as defined by the provisioning client.",
            "example": "E012345",
            "type": "string"
          },
          "name": {
            "properties": {
              "familyName": {
                "description": "The family name of the user.",
                "example": "Octocat",
                "type": "string"
              },
              "formatted": {
                "description": "The full name, including all middle names, titles, and suffixes as appropriate, formatted for display.",
                "example": "Ms. Mona Lisa Octocat",
                "type": "string"
              },
              "givenName": {
                "description": "The given name of the user.",
                "example": "Mona",
                "type": "string"
              },
              "middleName": {
                "description": "The middle name(s) of the user.",
                "example": "Lisa",
                "type": "string"
              }
            },
            "required": [
              "familyName",
              "givenName"
            ],
            "type": "object"
          },
          "roles": {
            "description": "The roles assigned to the user.",
            "items": {
              "properties": {
                "display": {
                  "type": "string"
                },
                "primary": {
                  "description": "Is the role a primary role for the user.",
                  "example": false,
                  "type": "boolean"
                },
                "type": {
                  "type": "string"
                },
                "value": {
                  "description": "The role value representing a user role in GitHub.",
                  "enum": [
                    "User",
                    "user",
                    "27d9891d-2c17-4f45-a262-781a0e55c80a",
                    "Restricted User",
                    "restricted_user",
                    "1ebc4a02-e56c-43a6-92a5-02ee09b90824",
                    "Enterprise Owner",
                    "enterprise_owner",
                    "981df190-8801-4618-a08a-d91f6206c954",
                    "ba4987ab-a1c3-412a-b58c-360fc407cb10",
                    "Billing Manager",
                    "billing_manager",
                    "0e338b8c-cc7f-498a-928d-ea3470d7e7e3",
                    "e6be2762-e4ad-4108-b72d-1bbe884a0f91"
                  ],
                  "example": "user",
                  "type": "string"
                }
              },
              "required": [
                "value"
              ],
              "type": "object"
            },
            "type": "array"
          },
          "schemas": {
            "description": "The URIs that are used to indicate the namespaces of the SCIM schemas.",
            "example": [
              "urn:ietf:params:scim:schemas:core:2.0:User"
            ],
            "items": {
              "enum": [
                "urn:ietf:params:scim:schemas:core:2.0:User"
              ],
              "type": "string"
            },
            "type": "array"
          },
          "userName": {
            "description": "The username for the user.",
            "example": "E012345",
            "type": "string"
          }
        },
        "required": [
          "schemas",
          "externalId",
          "userName",
          "active",
          "displayName",
          "emails"
        ],
        "type": "object"
      }
    }
  },
  "required": true
}
