http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "examples": {
        "default": {
          "value": {
            "base": "master",
            "body": "updated body",
            "state": "open",
            "title": "new title"
          }
        }
      },
      "schema": {
        "properties": {
          "base": {
            "description": "The name of the branch you want your changes pulled into. This should be an existing branch on the current repository. You cannot update the base branch on a pull request to point to another repository.",
            "type": "string"
          },
          "body": {
            "description": "The contents of the pull request.",
            "type": "string"
          },
          "maintainer_can_modify": {
            "description": "Indicates whether [maintainers can modify](https://docs.github.com/enterprise-server@3.8/articles/allowing-changes-to-a-pull-request-branch-created-from-a-fork/) the pull request.",
            "type": "boolean"
          },
          "state": {
            "description": "State of this Pull Request. Either `open` or `closed`.",
            "enum": [
              "open",
              "closed"
            ],
            "type": "string"
          },
          "title": {
            "description": "The title of the pull request.",
            "type": "string"
          }
        },
        "type": "object"
      }
    }
  },
  "required": false
}
