http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "examples": {
        "default": {
          "value": {
            "expected_head_sha": "6dcb09b5b57875f334f61aebed695e2e4193db5e"
          }
        }
      },
      "schema": {
        "nullable": true,
        "properties": {
          "expected_head_sha": {
            "description": "The expected SHA of the pull request's HEAD ref. This is the most recent commit on the pull request's branch. If the expected SHA does not match the pull request's HEAD, you will receive a `422 Unprocessable Entity` status. You can use the \"[List commits](https://docs.github.com/enterprise-server@3.8/rest/reference/repos#list-commits)\" endpoint to find the most recent commit SHA. Default: SHA of the pull request's current HEAD ref.",
            "type": "string"
          }
        },
        "type": "object"
      }
    }
  },
  "required": false
}
