http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "required": [
          "inputs"
        ],
        "type": "object",
        "properties": {
          "inputs": {
            "type": "array",
            "items": {
              "required": [
                "associations",
                "properties"
              ],
              "type": "object",
              "properties": {
                "associations": {
                  "type": "array",
                  "items": {
                    "required": [
                      "to",
                      "types"
                    ],
                    "type": "object",
                    "properties": {
                      "types": {
                        "type": "array",
                        "items": {
                          "required": [
                            "associationCategory",
                            "associationTypeId"
                          ],
                          "type": "object",
                          "properties": {
                            "associationCategory": {
                              "type": "string",
                              "description": "The category of the association, such as \"HUBSPOT_DEFINED\".",
                              "enum": [
                                "HUBSPOT_DEFINED",
                                "USER_DEFINED",
                                "INTEGRATOR_DEFINED"
                              ]
                            },
                            "associationTypeId": {
                              "type": "integer",
                              "description": "The ID representing the specific type of association.",
                              "format": "int32"
                            }
                          },
                          "description": "Defines the type, direction, and details of the relationship between two CRM objects."
                        }
                      },
                      "to": {
                        "required": [
                          "id"
                        ],
                        "type": "object",
                        "properties": {
                          "id": {
                            "type": "string"
                          }
                        }
                      }
                    }
                  }
                },
                "objectWriteTraceId": {
                  "type": "string"
                },
                "properties": {
                  "type": "object",
                  "additionalProperties": {
                    "type": "string"
                  }
                }
              }
            }
          }
        }
      }
    }
  },
  "required": true
}
