http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "required": [
          "objectIdToMerge",
          "primaryObjectId"
        ],
        "type": "object",
        "properties": {
          "objectIdToMerge": {
            "type": "string",
            "description": "The ID of the company to merge into the primary."
          },
          "primaryObjectId": {
            "type": "string",
            "description": "The ID of the primary company, which the other will merge into."
          }
        }
      }
    }
  },
  "required": true
}
