http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "required": [
          "listId",
          "newFolderId"
        ],
        "type": "object",
        "properties": {
          "listId": {
            "type": "string",
            "description": "The Id of the list to move."
          },
          "newFolderId": {
            "type": "string",
            "description": "The Id of folder to move the list to, the root folder is Id 0."
          }
        }
      }
    }
  },
  "required": true
}
