http_endpoint.request_body_spec:
{
  "description": "The IDs of the records to add and/or remove from the list.",
  "content": {
    "application/json": {
      "schema": {
        "required": [
          "recordIdsToAdd",
          "recordIdsToRemove"
        ],
        "type": "object",
        "properties": {
          "recordIdsToRemove": {
            "type": "array",
            "items": {
              "type": "string"
            }
          },
          "recordIdsToAdd": {
            "type": "array",
            "items": {
              "type": "string"
            }
          }
        },
        "description": "The IDs of the records to add and/or remove from a list.",
        "example": {
          "recordIdsToAdd": [
            "123",
            "456",
            "789"
          ],
          "recordIdsToRemove": [
            "654"
          ]
        }
      }
    }
  },
  "required": true
}
