http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "required": [
          "inputs"
        ],
        "type": "object",
        "properties": {
          "inputs": {
            "type": "array",
            "items": {
              "required": [
                "appId",
                "externalAccountId",
                "externalEventId"
              ],
              "type": "object",
              "properties": {
                "externalAccountId": {
                  "type": "string",
                  "description": "The accountId that is associated with this marketing event in the external event application."
                },
                "externalEventId": {
                  "type": "string",
                  "description": "The id of the marketing event in the external event application."
                },
                "appId": {
                  "type": "integer",
                  "description": "The id of the application that created the marketing event in HubSpot.",
                  "format": "int32"
                }
              }
            }
          }
        }
      }
    }
  },
  "required": true
}
