http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "required": [
          "inputs"
        ],
        "type": "object",
        "properties": {
          "inputs": {
            "type": "array",
            "items": {
              "required": [
                "eventName",
                "eventOrganizer",
                "externalAccountId",
                "externalEventId"
              ],
              "type": "object",
              "properties": {
                "startDateTime": {
                  "type": "string",
                  "description": "The start date and time of the marketing event.",
                  "format": "date-time"
                },
                "customProperties": {
                  "type": "array",
                  "description": "A list of PropertyValues. These can be whatever kind of property names and values you want. However, they must already exist on the HubSpot account's definition of the MarketingEvent Object. If they don't they will be filtered out and not set.\nIn order to do this you'll need to create a new PropertyGroup on the HubSpot account's MarketingEvent object for your specific app and create the Custom Property you want to track on that HubSpot account. Do not create any new default properties on the MarketingEvent object as that will apply to all HubSpot accounts.\n",
                  "items": {
                    "required": [
                      "name",
                      "sourceUpstreamDeployable",
                      "value"
                    ],
                    "type": "object",
                    "properties": {
                      "sourceId": {
                        "type": "string",
                        "description": "The ID of the property source indicating where it was created.",
                        "example": ""
                      },
                      "selectedByUser": {
                        "type": "boolean",
                        "description": "Whether the value was selected by a user.",
                        "example": false
                      },
                      "sourceLabel": {
                        "type": "string",
                        "description": "A human-readable label.",
                        "example": ""
                      },
                      "sourceUpstreamDeployable": {
                        "type": "string"
                      },
                      "source": {
                        "type": "string",
                        "description": "The origin of the property value, such as \"IMPORT\" or \"API\".",
                        "example": "",
                        "enum": [
                          "UNKNOWN",
                          "IMPORT",
                          "API",
                          "FORM",
                          "ANALYTICS",
                          "MIGRATION",
                          "SALESFORCE",
                          "INTEGRATION",
                          "CONTACTS_WEB",
                          "WAL_INCREMENTAL",
                          "TASK",
                          "EMAIL",
                          "WORKFLOWS",
                          "CALCULATED",
                          "SOCIAL",
                          "BATCH_UPDATE",
                          "SIGNALS",
                          "BIDEN",
                          "DEFAULT",
                          "COMPANIES",
                          "DEALS",
                          "ASSISTS",
                          "PRESENTATIONS",
                          "TALLY",
                          "SIDEKICK",
                          "CRM_UI",
                          "MERGE_CONTACTS",
                          "PORTAL_USER_ASSOCIATOR",
                          "INTEGRATIONS_PLATFORM",
                          "BCC_TO_CRM",
                          "FORWARD_TO_CRM",
                          "ENGAGEMENTS",
                          "SALES",
                          "HEISENBERG",
                          "LEADIN",
                          "GMAIL_INTEGRATION",
                          "ACADEMY",
                          "SALES_MESSAGES",
                          "AVATARS_SERVICE",
                          "MERGE_COMPANIES",
                          "SEQUENCES",
                          "COMPANY_FAMILIES",
                          "MOBILE_IOS",
                          "MOBILE_ANDROID",
                          "CONTACTS",
                          "ASSOCIATIONS",
                          "EXTENSION",
                          "SUCCESS",
                          "BOT",
                          "INTEGRATIONS_SYNC",
                          "AUTOMATION_PLATFORM",
                          "CONVERSATIONS",
                          "EMAIL_INTEGRATION",
                          "CONTENT_MEMBERSHIP",
                          "QUOTES",
                          "BET_ASSIGNMENT",
                          "QUOTAS",
                          "BET_CRM_CONNECTOR",
                          "MEETINGS",
                          "MERGE_OBJECTS",
                          "RECYCLING_BIN",
                          "ADS",
                          "AI_GROUP",
                          "COMMUNICATOR",
                          "SETTINGS",
                          "PROPERTY_SETTINGS",
                          "PIPELINE_SETTINGS",
                          "COMPANY_INSIGHTS",
                          "BEHAVIORAL_EVENTS",
                          "PAYMENTS",
                          "GOALS",
                          "PORTAL_OBJECT_SYNC",
                          "APPROVALS",
                          "FILE_MANAGER",
                          "MARKETPLACE",
                          "INTERNAL_PROCESSING",
                          "FORECASTING",
                          "SLACK_INTEGRATION",
                          "CRM_UI_BULK_ACTION",
                          "WORKFLOW_CONTACT_DELETE_ACTION",
                          "ACCEPTANCE_TEST",
                          "PLAYBOOKS",
                          "CHATSPOT",
                          "FLYWHEEL_PRODUCT_DATA_SYNC",
                          "HELP_DESK",
                          "BILLING",
                          "DATA_ENRICHMENT",
                          "AUTOMATION_JOURNEY",
                          "MICROAPPS",
                          "INTENT",
                          "PROSPECTING_AGENT",
                          "CENTRAL_EXCHANGE_RATES",
                          "HELP_DESK_AI",
                          "CONVERSATIONAL_ENRICHMENT",
                          "CRM_PROCESSES_PLATFORM",
                          "CLONE_OBJECTS",
                          "MARKET_SOURCING",
                          "DATASET",
                          "PROPERTY_RESTORE"
                        ]
                      },
                      "updatedByUserId": {
                        "type": "integer",
                        "description": "The ID of the user who updated the property.",
                        "format": "int32"
                      },
                      "persistenceTimestamp": {
                        "type": "integer",
                        "format": "int64"
                      },
                      "sourceMetadata": {
                        "type": "string",
                        "description": "Source metadata encoded as a base64 string. For example: `ZXhhbXBsZSBzdHJpbmc=`",
                        "example": ""
                      },
                      "dataSensitivity": {
                        "type": "string",
                        "description": "The sensitivity level of the property, such as \"non_sensitive\", \"sensitive\", and \"highly_sensitive\".",
                        "example": "",
                        "enum": [
                          "none",
                          "standard",
                          "high"
                        ]
                      },
                      "sourceVid": {
                        "type": "array",
                        "description": "The unique identifier associated with the source.",
                        "example": "",
                        "items": {
                          "type": "integer",
                          "format": "int64"
                        }
                      },
                      "unit": {
                        "type": "string",
                        "description": "The unit of measurement or context for the value.",
                        "example": ""
                      },
                      "requestId": {
                        "type": "string",
                        "description": "A unique ID associated with this request.",
                        "example": ""
                      },
                      "isEncrypted": {
                        "type": "boolean",
                        "description": "Whether the property value is encrypted.",
                        "example": false
                      },
                      "name": {
                        "type": "string",
                        "description": "Name of custom property",
                        "example": ""
                      },
                      "useTimestampAsPersistenceTimestamp": {
                        "type": "boolean"
                      },
                      "value": {
                        "type": "string",
                        "description": "Custom property value",
                        "example": ""
                      },
                      "selectedByUserTimestamp": {
                        "type": "integer",
                        "description": "The timestamp when the value was selected by a user, if applicable.",
                        "format": "int64"
                      },
                      "timestamp": {
                        "type": "integer",
                        "description": "When the value was set, as a 64-bit integer.",
                        "format": "int64"
                      },
                      "isLargeValue": {
                        "type": "boolean"
                      }
                    },
                    "description": "Represents a single custom property of a marketing event, storing its name, value, metadata (like source, timestamp, and sensitivity), and related audit information for tracking changes."
                  }
                },
                "externalAccountId": {
                  "type": "string",
                  "description": "The accountId that is associated with this marketing event in the external event application."
                },
                "eventCancelled": {
                  "type": "boolean",
                  "description": "Indicates if the marketing event has been cancelled.  Defaults to `false`"
                },
                "eventOrganizer": {
                  "type": "string",
                  "description": "The name of the organizer of the marketing event."
                },
                "eventUrl": {
                  "type": "string",
                  "description": "A URL in the external event application where the marketing event can be managed."
                },
                "externalEventId": {
                  "type": "string",
                  "description": "The id of the marketing event in the external event application."
                },
                "eventDescription": {
                  "type": "string",
                  "description": "The description of the marketing event."
                },
                "eventName": {
                  "type": "string",
                  "description": "The name of the marketing event."
                },
                "eventType": {
                  "type": "string",
                  "description": "Describes what type of event this is.  For example: `WEBINAR`, `CONFERENCE`, `WORKSHOP`"
                },
                "eventCompleted": {
                  "type": "boolean"
                },
                "endDateTime": {
                  "type": "string",
                  "description": "The end date and time of the marketing event.",
                  "format": "date-time"
                }
              }
            }
          }
        }
      }
    }
  },
  "required": true
}
