http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "description": "Serializer mixin that resolves appropriate response for tags depending on license.",
        "properties": {
          "id": {
            "type": "integer",
            "readOnly": true
          },
          "name": {
            "type": "string",
            "nullable": true,
            "maxLength": 400
          },
          "description": {
            "type": "string"
          },
          "pinned": {
            "type": "boolean"
          },
          "created_at": {
            "type": "string",
            "format": "date-time",
            "readOnly": true
          },
          "created_by": {
            "allOf": [
              {
                "type": "object",
                "properties": {
                  "id": {
                    "type": "integer",
                    "readOnly": true
                  },
                  "uuid": {
                    "type": "string",
                    "format": "uuid",
                    "readOnly": true
                  },
                  "distinct_id": {
                    "type": "string",
                    "nullable": true,
                    "maxLength": 200
                  },
                  "first_name": {
                    "type": "string",
                    "maxLength": 150
                  },
                  "last_name": {
                    "type": "string",
                    "maxLength": 150
                  },
                  "email": {
                    "type": "string",
                    "format": "email",
                    "title": "Email address",
                    "maxLength": 254
                  },
                  "is_email_verified": {
                    "type": "boolean",
                    "nullable": true
                  },
                  "hedgehog_config": {
                    "type": "object",
                    "additionalProperties": {},
                    "nullable": true,
                    "readOnly": true
                  },
                  "role_at_organization": {
                    "nullable": true,
                    "oneOf": [
                      {
                        "enum": [
                          "engineering",
                          "data",
                          "product",
                          "founder",
                          "leadership",
                          "marketing",
                          "sales",
                          "other"
                        ],
                        "type": "string",
                        "description": "* `engineering` - Engineering\n* `data` - Data\n* `product` - Product Management\n* `founder` - Founder\n* `leadership` - Leadership\n* `marketing` - Marketing\n* `sales` - Sales / Success\n* `other` - Other"
                      },
                      {
                        "enum": [
                          ""
                        ]
                      },
                      {
                        "enum": [
                          null
                        ]
                      }
                    ]
                  }
                },
                "required": [
                  "email",
                  "hedgehog_config",
                  "id",
                  "uuid"
                ]
              }
            ],
            "readOnly": true
          },
          "last_accessed_at": {
            "type": "string",
            "format": "date-time",
            "nullable": true
          },
          "is_shared": {
            "type": "boolean",
            "readOnly": true
          },
          "deleted": {
            "type": "boolean"
          },
          "creation_mode": {
            "allOf": [
              {
                "enum": [
                  "default",
                  "template",
                  "duplicate"
                ],
                "type": "string",
                "description": "* `default` - Default\n* `template` - Template\n* `duplicate` - Duplicate"
              }
            ],
            "readOnly": true
          },
          "filters": {
            "type": "object",
            "additionalProperties": {},
            "readOnly": true
          },
          "variables": {
            "type": "object",
            "additionalProperties": {},
            "nullable": true,
            "readOnly": true
          },
          "breakdown_colors": {},
          "data_color_theme_id": {
            "type": "integer",
            "nullable": true
          },
          "tags": {
            "type": "array",
            "items": {}
          },
          "restriction_level": {
            "allOf": [
              {
                "enum": [
                  21,
                  37
                ],
                "type": "integer",
                "description": "* `21` - Everyone in the project can edit\n* `37` - Only those invited to this dashboard can edit"
              }
            ],
            "minimum": 0,
            "maximum": 32767
          },
          "effective_restriction_level": {
            "allOf": [
              {
                "enum": [
                  21,
                  37
                ],
                "type": "integer"
              }
            ],
            "readOnly": true
          },
          "effective_privilege_level": {
            "allOf": [
              {
                "enum": [
                  21,
                  37
                ],
                "type": "integer"
              }
            ],
            "readOnly": true
          },
          "user_access_level": {
            "type": "string",
            "nullable": true,
            "readOnly": true,
            "description": "The effective access level the user has for this object"
          },
          "access_control_version": {
            "type": "string",
            "readOnly": true
          },
          "last_refresh": {
            "type": "string",
            "format": "date-time",
            "nullable": true
          },
          "persisted_filters": {
            "type": "object",
            "additionalProperties": {},
            "nullable": true,
            "readOnly": true
          },
          "persisted_variables": {
            "type": "object",
            "additionalProperties": {},
            "nullable": true,
            "readOnly": true
          },
          "team_id": {
            "type": "integer",
            "readOnly": true
          },
          "tiles": {
            "type": "array",
            "items": {
              "type": "object",
              "additionalProperties": {}
            },
            "nullable": true,
            "readOnly": true
          },
          "use_template": {
            "type": "string",
            "writeOnly": true
          },
          "use_dashboard": {
            "type": "integer",
            "writeOnly": true,
            "nullable": true
          },
          "delete_insights": {
            "type": "boolean",
            "writeOnly": true,
            "default": false
          },
          "_create_in_folder": {
            "type": "string",
            "writeOnly": true,
            "title": " create in folder"
          }
        },
        "required": [
          "access_control_version",
          "created_at",
          "created_by",
          "creation_mode",
          "effective_privilege_level",
          "effective_restriction_level",
          "filters",
          "id",
          "is_shared",
          "persisted_filters",
          "persisted_variables",
          "team_id",
          "tiles",
          "user_access_level",
          "variables"
        ]
      }
    }
  }
}
