http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "example": {
          "reactivate": false,
          "remote_provider_name": "ACCOUNTING_SEED"
        },
        "properties": {
          "reactivate": {
            "default": false,
            "deprecated": true,
            "description": "Deprecated, use POST accounting/connection/<connection_id>/reactivate.",
            "type": "boolean"
          },
          "remote_provider_name": {
            "description": "The name of the accounting connection.",
            "type": "string"
          }
        },
        "required": [
          "remote_provider_name"
        ],
        "type": "object"
      }
    }
  },
  "required": true
}
