http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "example": {
          "table_label": "Teams",
          "table_name": "teams",
          "type": "custom_table"
        },
        "properties": {
          "table_label": {
            "description": "The custom table's label as displayed in Ramp",
            "type": "string"
          },
          "table_name": {
            "description": "The custom table's API name",
            "type": "string"
          },
          "type": {
            "default": "custom_table",
            "enum": [
              "custom_table"
            ],
            "type": "string"
          }
        },
        "required": [
          "table_label",
          "table_name"
        ],
        "type": "object"
      }
    }
  },
  "required": true
}
