http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "discriminator": {
          "mapping": {
            "boolean": "#/components/schemas/DeveloperApiCreatePrimitiveColumnRequestBody",
            "custom_reference": "#/components/schemas/DeveloperApiCreateReferenceColumnRequestBody",
            "native_reference": "#/components/schemas/DeveloperApiCreateReferenceColumnRequestBody",
            "text": "#/components/schemas/DeveloperApiCreatePrimitiveColumnRequestBody"
          },
          "propertyName": "column_type_name"
        },
        "oneOf": [
          {
            "example": {
              "column_type_name": "text",
              "label": "Project Name",
              "name": "project_name"
            },
            "properties": {
              "column_type_name": {
                "description": "The type of the primitive column.",
                "enum": [
                  "boolean",
                  "text"
                ],
                "type": "string"
              },
              "label": {
                "description": "The display name of the column",
                "type": "string"
              },
              "name": {
                "description": "The API name of the column, to be used when writing to this column",
                "type": "string"
              }
            },
            "required": [
              "column_type_name",
              "label",
              "name"
            ],
            "type": "object"
          },
          {
            "example": {
              "column_type_name": "custom_reference",
              "label": "Cost Center",
              "name": "cost_center",
              "reference_args": {
                "cardinality": "many_to_one",
                "corresponding_column_label": "Members",
                "corresponding_column_name": "members",
                "foreign_table": {
                  "table_name": "cost_center",
                  "type": "custom_table"
                }
              }
            },
            "properties": {
              "column_type_name": {
                "description": "The type of the reference column.",
                "enum": [
                  "custom_reference",
                  "native_reference"
                ],
                "type": "string"
              },
              "label": {
                "description": "The display name of the column",
                "type": "string"
              },
              "name": {
                "description": "The API name of the column, to be used when writing to this column",
                "type": "string"
              },
              "reference_args": {
                "allOf": [
                  {
                    "example": {
                      "cardinality": "many_to_one",
                      "corresponding_column_label": "Members",
                      "corresponding_column_name": "members",
                      "foreign_table": {
                        "table_name": "cost_center",
                        "type": "custom_table"
                      }
                    },
                    "properties": {
                      "cardinality": {
                        "description": "The cardinality of the reference.",
                        "enum": [
                          "many_to_many",
                          "many_to_one"
                        ],
                        "type": "string"
                      },
                      "corresponding_column_label": {
                        "default": null,
                        "description": "The label of the corresponding column. Will be generated if not provided.",
                        "nullable": true,
                        "type": "string"
                      },
                      "corresponding_column_name": {
                        "description": "The API name of the corresponding column.",
                        "type": "string"
                      },
                      "foreign_table": {
                        "description": "The table that this column references.",
                        "oneOf": [
                          {
                            "example": {
                              "table_name": "business_regions",
                              "type": "custom_table"
                            },
                            "properties": {
                              "table_name": {
                                "description": "The name of the custom table",
                                "type": "string"
                              },
                              "type": {
                                "default": "custom_table",
                                "enum": [
                                  "custom_table"
                                ],
                                "type": "string"
                              }
                            },
                            "required": [
                              "table_name"
                            ],
                            "type": "object"
                          },
                          {
                            "example": {
                              "table_name": "users",
                              "type": "native_table"
                            },
                            "properties": {
                              "table_name": {
                                "description": "The name of the native Ramp table",
                                "enum": [
                                  "accounting_field_options",
                                  "bills",
                                  "business_entities",
                                  "cards",
                                  "departments",
                                  "limits",
                                  "locations",
                                  "purchase_orders",
                                  "reimbursements",
                                  "transactions",
                                  "users"
                                ],
                                "type": "string"
                              },
                              "type": {
                                "default": "native_table",
                                "enum": [
                                  "native_table"
                                ],
                                "type": "string"
                              }
                            },
                            "required": [
                              "table_name"
                            ],
                            "type": "object"
                          }
                        ]
                      }
                    },
                    "required": [
                      "cardinality",
                      "corresponding_column_name",
                      "foreign_table"
                    ],
                    "type": "object"
                  }
                ],
                "description": "Arguments for creating a reference column."
              }
            },
            "required": [
              "column_type_name",
              "label",
              "name",
              "reference_args"
            ],
            "type": "object"
          }
        ]
      }
    }
  },
  "required": true
}
