http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "example": {
          "additional_headers": {
            "Authorization": "Bearer token123",
            "X-Custom-Header": "value"
          },
          "endpoint_url": "https://example.com/webhook",
          "event_types": [
            "TRANSACTION_REFUNDED",
            "USER_INVITED"
          ]
        },
        "properties": {
          "additional_headers": {
            "additionalProperties": {
              "maxLength": 1000,
              "minLength": 1,
              "type": "string"
            },
            "description": "Optional headers to include with webhook requests. Max 5 headers, header names max 100 chars, values max 1000 chars.",
            "maxLength": 5,
            "type": "object"
          },
          "endpoint_url": {
            "description": "URL where webhook events will be sent",
            "format": "url",
            "type": "string"
          },
          "event_types": {
            "description": "Events that this subscription will receive",
            "items": {
              "enum": [
                "bills.approved",
                "bills.created",
                "bills.paid",
                "bills.rejected",
                "purchase_orders.created",
                "purchase_orders.updated",
                "reimbursements.ready_for_review",
                "reimbursements.ready_to_sync",
                "tests.test_event",
                "transactions.authorized",
                "transactions.cleared",
                "transactions.ready_for_review",
                "transactions.ready_to_sync",
                "unified_requests.external_approval_request",
                "vendors.activated",
                "webhooks.verification"
              ],
              "type": "string"
            },
            "type": "array"
          }
        },
        "required": [
          "endpoint_url",
          "event_types"
        ],
        "type": "object"
      }
    }
  },
  "required": true
}
