http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "example": {
          "data": [
            {
              "cells": [
                {
                  "contents": "California",
                  "name": "home_state"
                },
                {
                  "contents": {
                    "column_name": "email",
                    "value": "<another_existing_ramp_user_email>"
                  },
                  "name": "budget_managers"
                },
                {
                  "contents": {
                    "column_name": "email",
                    "value": "<existing_ramp_user_email>"
                  },
                  "name": "budget_managers"
                }
              ],
              "row": {
                "column_name": "email",
                "value": "<existing_ramp_user_email>"
              }
            },
            {
              "cells": [
                {
                  "contents": "New York",
                  "name": "home_state"
                },
                {
                  "contents": null,
                  "name": "budget_managers"
                }
              ],
              "row": {
                "column_name": "id",
                "value": "<existing_ramp_user_uuid>"
              }
            }
          ]
        },
        "properties": {
          "data": {
            "description": "List of rows to insert or update. All entries must have the same set of column names, with `null` provided to indicate that a value should not be set.",
            "items": {
              "properties": {
                "cells": {
                  "description": "The contents of the cells in the row. The column name should be the name of the column, and the contents should be the value to insert into the cell. When setting a cell to reference many rows, each reference must be a separate entry in the list.",
                  "items": {
                    "properties": {
                      "contents": {
                        "nullable": true,
                        "oneOf": [
                          {
                            "properties": {
                              "column_name": {
                                "title": "Column Name",
                                "type": "string"
                              },
                              "value": {
                                "title": "Value",
                                "type": "string"
                              }
                            },
                            "required": [
                              "column_name",
                              "value"
                            ],
                            "title": "NativeRowIdentifier",
                            "type": "object"
                          },
                          {
                            "type": "boolean"
                          },
                          {
                            "type": "string"
                          }
                        ],
                        "title": "Contents"
                      },
                      "name": {
                        "title": "Name",
                        "type": "string"
                      }
                    },
                    "required": [
                      "contents",
                      "name"
                    ],
                    "title": "ColumnNameAndContents",
                    "type": "object"
                  },
                  "title": "Cells",
                  "type": "array"
                },
                "row": {
                  "allOf": [
                    {
                      "properties": {
                        "column_name": {
                          "title": "Column Name",
                          "type": "string"
                        },
                        "value": {
                          "title": "Value",
                          "type": "string"
                        }
                      },
                      "required": [
                        "column_name",
                        "value"
                      ],
                      "title": "NativeRowIdentifier",
                      "type": "object"
                    }
                  ],
                  "description": "An identifier for the Ramp object"
                }
              },
              "required": [
                "cells",
                "row"
              ],
              "title": "NativeRowColumnContentsByColumnName",
              "type": "object"
            },
            "title": "Data",
            "type": "array"
          }
        },
        "required": [
          "data"
        ],
        "title": "PutNativeRow",
        "type": "object"
      }
    }
  },
  "required": true
}
