http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "example": {
          "token": "ramp_tok_IwOGYzYTlmM2YxOTQ5MGE3YmNmMDFkNTVk",
          "token_type_hint": "refresh_token"
        },
        "properties": {
          "token": {
            "description": "Access token or refresh token to be revoked.",
            "type": "string"
          },
          "token_type_hint": {
            "description": "Type of the token to be revoked.",
            "enum": [
              "access_token",
              "refresh_token"
            ],
            "type": "string"
          }
        },
        "required": [
          "token"
        ],
        "type": "object"
      }
    }
  },
  "required": true
}
