                "field_option_external_id": {
                  "description": "Remote ID of accounting field option. This is the external ID, likely from ERP system.",
                  "type": "string"
                }
              },
              "required": [
                "field_external_id",
                "field_option_external_id"
              ],
              "type": "object"
            },
            "type": "array"
          },
          "due_at": {
            "description": "Due date of the bill.",
            "format": "date",
            "type": "string"
          },
          "entity_id": {
            "description": "Associated business entity.",
            "format": "uuid",
            "type": "string"
          },
