                  "type": "string"
                },
                "unit_price": {
                  "minimum": 0,
                  "oneOf": [
                    {
                      "format": "decimal",
                      "type": "string"
                    },
                    {
                      "type": "number"
                    }
                  ]
                },
                "unit_quantity": {
                  "minimum": 0,
                  "type": "integer"
                }
              },
