            "nullable": true,
            "type": "string"
          },
          "remote_id": {
            "description": "An ID that identifies the bill on client's side.",
            "nullable": true,
            "type": "string"
          },
          "vendor_contact_id": {
            "description": "Unique identifier of vendor contact, must be associated with passed vendor.",
            "format": "uuid",
            "type": "string"
          },
          "vendor_id": {
            "description": "Unique identifier of bill vendor.",
            "format": "uuid",
            "type": "string"
          }
