http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "example": {
          "account_nickname": "Primary Business Account",
          "ach_details": {
            "account_name": "Business Account",
            "account_number": "123456789",
            "account_owner_type": "BUSINESS",
            "account_type": "Checking",
            "routing_number": "021000021"
          },
          "is_default": false
        },
        "properties": {
          "account_nickname": {
            "description": "Optional nickname for the payment account",
            "type": "string"
          },
          "ach_details": {
            "allOf": [
              {
                "properties": {
                  "account_name": {
                    "default": "Business Account",
                    "description": "Account name",
                    "type": "string"
                  },
                  "account_number": {
                    "description": "ACH account number",
                    "type": "string"
                  },
                  "account_owner_type": {
                    "default": "BUSINESS",
                    "description": "Account owner type",
                    "enum": [
                      "BUSINESS",
                      "INDIVIDUAL"
                    ],
                    "type": "string"
                  },
                  "account_type": {
                    "default": "Checking",
                    "description": "Account type",
                    "enum": [
                      "Checking",
                      "Savings"
                    ],
                    "type": "string"
                  },
                  "routing_number": {
                    "description": "ACH routing number",
                    "type": "string"
                  }
                },
                "required": [
                  "account_number",
                  "routing_number"
                ],
                "type": "object"
              }
            ],
            "description": "ACH payment details"
          },
          "is_default": {
            "default": false,
            "description": "Whether this should be the default payment method",
            "type": "boolean"
          },
          "wire_details": {
            "allOf": [
              {
                "properties": {
                  "account_name": {
                    "default": "Business Account",
                    "description": "Account name",
                    "type": "string"
                  },
                  "account_number": {
                    "description": "Wire account number",
                    "type": "string"
                  },
                  "routing_number": {
                    "description": "Wire routing number",
                    "type": "string"
                  }
                },
                "required": [
                  "account_number",
                  "routing_number"
                ],
                "type": "object"
              }
            ],
            "description": "Wire payment details"
          }
        },
        "type": "object"
      }
    }
  },
  "required": true
}
