http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "example": {
          "accounting_connection_id": null,
          "gl_accounts": [
            {
              "classification": "EXPENSE",
              "code": "6410",
              "id": "514",
              "name": "Employees:Salaries & Wages"
            },
            {
              "classification": "EXPENSE",
              "id": "418",
              "name": "Office/Admin:Phone & Internet"
            },
            {
              "classification": "REVENUE",
              "id": "425",
              "name": "Sales"
            }
          ]
        },
        "properties": {
          "gl_accounts": {
            "description": "A list of general ledger accounts that you want to use to classify your Ramp transactions.",
            "items": {
              "example": {
                "classification": "EXPENSE",
                "code": "6410",
                "id": "514",
                "name": "Employees:Salaries & Wages"
              },
              "properties": {
                "classification": {
                  "enum": [
                    "ANY",
                    "ASSET",
                    "CREDCARD",
                    "EQUITY",
                    "EXPENSE",
                    "LIABILITY",
                    "REVENUE",
                    "UNKNOWN"
                  ],
                  "type": "string"
                },
                "code": {
                  "description": "e.g. 400-100.",
                  "type": "string"
                },
                "id": {
                  "description": "Remote/external ID of GL account in ERP system.",
                  "type": "string"
                },
                "name": {
                  "description": "e.g. Travel : Travel - Lodging.",
                  "type": "string"
                }
              },
              "required": [
                "classification",
                "id",
                "name"
              ],
              "type": "object"
            },
            "maxItems": 500,
            "minItems": 1,
            "type": "array"
          }
        },
        "required": [
          "gl_accounts"
        ],
        "type": "object"
      }
    }
  },
  "required": true
}
