http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "properties": {
          "is_memo_recurring": {
            "default": false,
            "deprecated": true,
            "description": "Whether this memo should automatically apply to similar future transactions.",
            "type": "boolean"
          },
          "memo": {
            "description": "Text content of the memo to create.",
            "maxLength": 255,
            "type": "string"
          }
        },
        "required": [
          "memo"
        ],
        "type": "object"
      }
    }
  },
  "required": true
}
